  function elAt(attrs) {
    var s = '';
    for (var key in attrs) {
      var k = key;
      if (key === 'clazz') {k = 'class';}
      s = s + ' ' + k + '="' + attrs[key] + '"';
  	}
    return s;
  }

  function el() {
    var s = '<'+this.eln;
    for (var i = 0; i<arguments.length; i++) {
      var val = arguments[i];
      var type = typeof val;

      if (i==0 && type === 'object') {s = s + elAt(val) + ((this.empty) ? '/>' :'>'); continue;}
      else if (i==0) {s = s + ((this.empty) ? '/>' :'>');}

      if (this.empty) return s;

      if (type !== 'string') continue;

      s = s + val;
    }

  	s = s + '</'+this.eln+'>';
  	return s;
  }


